#include <iostream>\n\nint main() {\n    int n, first = 0, second = 1, next;\n\n    std::cout << \"Enter the number of terms: \";\n    std::cin >> n;\n\n    std::cout << \"Fibonacci Series: \";\n\n    for (int i = 0; i < n; i++) {\n        if (i <= 1) {\n            next = i;\n        } else {\n            next = first + second;\n            first = second;\n            second = next;\n        }\n        std::cout << next << \" \";\n    }\n    std::cout << std::endl;\n    return 0;\n}\n